#!/usr/bin/env bash
python clean-world-econ-data.py --file="data/econ-indicators.csv" --output-col-name="GNI per capita (constant 2005 US$)" --output-next-suffix="_next" --min-observations=5000 --desired-consecutive-years=40 --null-handling-mode="DROP" --output-file-name="data/cleaned_no_nulls.csv" --percent-in-validation=25 > no_nulls_out.txt &
python clean-world-econ-data.py --file="data/econ-indicators.csv" --output-col-name="GNI per capita (constant 2005 US$)" --output-next-suffix="_next" --min-observations=5000 --desired-consecutive-years=40 --null-handling-mode="IGNORE" --output-file-name="data/cleaned_all.csv" --percent-in-validation=25 > all_out.txt &
python clean-world-econ-data.py --file="data/econ-indicators.csv" --output-col-name="GNI per capita (constant 2005 US$)" --output-next-suffix="_next" --min-observations=10 --desired-consecutive-years=40 --null-handling-mode="IMPUTE" --output-file-name="data/imputed-data.csv" --percent-in-validation=25 > imputed_out.txt
